import React, { useState, useEffect } from 'react';
import { configApi } from '../api';

function Config() {
  const [configs, setConfigs] = useState([]);
  const [loading, setLoading] = useState(true);
  const [message, setMessage] = useState(null);
  const [editValues, setEditValues] = useState({});

  useEffect(() => {
    loadConfigs();
  }, []);

  const loadConfigs = async () => {
    try {
      setLoading(true);
      const res = await configApi.getAll();
      setConfigs(res.data || []);

      // Ìé∏ÏßëÏö© Í∞í Ï¥àÍ∏∞Ìôî
      const values = {};
      (res.data || []).forEach(config => {
        values[config.key] = config.value;
      });
      setEditValues(values);
    } catch (error) {
      setMessage({ type: 'error', text: error.message });
    } finally {
      setLoading(false);
    }
  };

  const handleChange = (key, value) => {
    setEditValues(prev => ({
      ...prev,
      [key]: value
    }));
  };

  const handleSave = async (key) => {
    try {
      await configApi.update(key, editValues[key]);
      setMessage({ type: 'success', text: `${key} ÏÑ§Ï†ïÏù¥ Ï†ÄÏû•ÎêòÏóàÏäµÎãàÎã§.` });
      loadConfigs();
    } catch (error) {
      setMessage({ type: 'error', text: error.message });
    }
  };

  const handleSaveAll = async () => {
    try {
      const configsToUpdate = Object.entries(editValues).map(([key, value]) => ({
        key,
        value
      }));
      await configApi.batchUpdate(configsToUpdate);
      setMessage({ type: 'success', text: 'Î™®Îì† ÏÑ§Ï†ïÏù¥ Ï†ÄÏû•ÎêòÏóàÏäµÎãàÎã§.' });
      loadConfigs();
    } catch (error) {
      setMessage({ type: 'error', text: error.message });
    }
  };

  const getConfigDescription = (key) => {
    const descriptions = {
      PROVIDER: 'LLM Ï†úÍ≥µÏûê (gemini ÎòêÎäî openai)',
      GEMINI_MODEL: 'Gemini Î™®Îç∏Î™Ö (Ïòà: gemini-2.5-pro)',
      OPENAI_MODEL: 'OpenAI Î™®Îç∏Î™Ö (Ïòà: gpt-4.1-mini)',
      TEMP_BASE: 'ÏÉùÏÑ± Temperature (0.0 ~ 1.0)',
      MAX_RETRY: 'ÏµúÎåÄ Ïû¨ÏãúÎèÑ ÌöüÏàò',
      LOG_LEVEL: 'Î°úÍ∑∏ Î†àÎ≤® (INFO, WARN, ERROR)'
    };
    return descriptions[key] || '';
  };

  const getInputType = (key) => {
    if (key === 'MAX_RETRY') return 'number';
    if (key === 'TEMP_BASE') return 'number';
    return 'text';
  };

  return (
    <div>
      <div className="flex-between mb-4">
        <h1>‚öôÔ∏è ÏÑ§Ï†ï</h1>
        <button className="btn btn-primary" onClick={handleSaveAll}>
          üíæ Ï†ÑÏ≤¥ Ï†ÄÏû•
        </button>
      </div>

      {message && (
        <div className={`alert alert-${message.type}`}>
          {message.text}
          <button
            onClick={() => setMessage(null)}
            style={{ float: 'right', background: 'none', border: 'none', cursor: 'pointer' }}
          >
            ‚úï
          </button>
        </div>
      )}

      {/* LLM ÏÑ§Ï†ï */}
      <div className="card">
        <div className="card-header">
          <h2>ü§ñ LLM ÏÑ§Ï†ï</h2>
        </div>

        {loading ? (
          <div className="loading">
            <div className="spinner"></div>
            <span>Î°úÎî© Ï§ë...</span>
