import React, { useState, useEffect } from 'react';
import { chartsApi } from '../api';

function Charts() {
  const [charts, setCharts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [message, setMessage] = useState(null);
  const [showModal, setShowModal] = useState(false);
  const [selectedChart, setSelectedChart] = useState(null);
  const [formData, setFormData] = useState({
    chart_id: '',
    chart_name: '',
    data: ''
  });

  useEffect(() => {
    loadCharts();
  }, []);

  const loadCharts = async () => {
    try {
      setLoading(true);
      const res = await chartsApi.getAll();
      setCharts(res.data || []);
    } catch (error) {
      setMessage({ type: 'error', text: error.message });
    } finally {
      setLoading(false);
    }
  };

  const handleCreate = async (e) => {
    e.preventDefault();
    try {
      let data = {};
      if (formData.data) {
        try {
          data = JSON.parse(formData.data);
        } catch (e) {
          setMessage({ type: 'error', text: 'JSON ÌòïÏãùÏù¥ Ïò¨Î∞îÎ•¥ÏßÄ ÏïäÏäµÎãàÎã§.' });
          return;
        }
      }

      await chartsApi.create({
        chart_id: formData.chart_id,
        chart_name: formData.chart_name,
        data: data
      });

      setMessage({ type: 'success', text: 'Ï∞®Ìä∏Í∞Ä ÏÉùÏÑ±ÎêòÏóàÏäµÎãàÎã§.' });
      setShowModal(false);
      setFormData({ chart_id: '', chart_name: '', data: '' });
      loadCharts();
    } catch (error) {
      setMessage({ type: 'error', text: error.message });
    }
  };

  const handleViewDetail = async (chartId) => {
    try {
      const res = await chartsApi.get(chartId);
      setSelectedChart(res.data);
    } catch (error) {
      setMessage({ type: 'error', text: error.message });
    }
  };

  const handleDelete = async (chartId) => {
    if (!window.confirm('Ïù¥ Ï∞®Ìä∏Î•º ÏÇ≠Ï†úÌïòÏãúÍ≤†ÏäµÎãàÍπå?')) return;

    try {
      await chartsApi.delete(chartId);
      setMessage({ type: 'success', text: 'Ï∞®Ìä∏Í∞Ä ÏÇ≠Ï†úÎêòÏóàÏäµÎãàÎã§.' });
      loadCharts();
    } catch (error) {
      setMessage({ type: 'error', text: error.message });
    }
  };

  return (
    <div>
      <div className="flex-between mb-4">
        <h1>üìà Ï∞®Ìä∏ Îç∞Ïù¥ÌÑ∞ Í¥ÄÎ¶¨</h1>
        <button className="btn btn-primary" onClick={() => setShowModal(true)}>
          ‚ûï ÏÉà Ï∞®Ìä∏
        </button>
      </div>

      {message && (
