import React, { useState, useEffect } from 'react';
import { logsApi } from '../api';

function Logs() {
  const [activeTab, setActiveTab] = useState('logs');
  const [logs, setLogs] = useState([]);
  const [errors, setErrors] = useState([]);
  const [stats, setStats] = useState(null);
  const [loading, setLoading] = useState(true);
  const [message, setMessage] = useState(null);
  const [filter, setFilter] = useState('');

  useEffect(() => {
    loadData();
  }, [activeTab, filter]);

  const loadData = async () => {
    try {
      setLoading(true);

      // ÌÜµÍ≥Ñ Î°úÎìú
      const statsRes = await logsApi.getStats();
      setStats(statsRes.data);

      if (activeTab === 'logs') {
        const params = filter ? { level: filter } : {};
        const res = await logsApi.getLogs(params);
        setLogs(res.data || []);
      } else {
        const res = await logsApi.getErrors();
        setErrors(res.data || []);
      }
    } catch (error) {
      setMessage({ type: 'error', text: error.message });
    } finally {
      setLoading(false);
    }
  };

  const handleClearLogs = async () => {
    const days = prompt('Î™á Ïùº Ïù¥Ï†ÑÏùò Î°úÍ∑∏Î•º ÏÇ≠Ï†úÌïòÏãúÍ≤†ÏäµÎãàÍπå?', '30');
    if (!days) return;

    try {
      const res = await logsApi.clear(parseInt(days));
      setMessage({ type: 'success', text: res.message });
      loadData();
    } catch (error) {
      setMessage({ type: 'error', text: error.message });
    }
  };

  const getLevelBadge = (level) => {
    const levelMap = {
      INFO: { class: 'badge-running', text: 'INFO' },
      WARN: { class: 'badge-pending', text: 'WARN' },
      ERROR: { class: 'badge-fail', text: 'ERROR' }
    };
    const info = levelMap[level] || { class: 'badge-pending', text: level };
    return <span className={`badge ${info.class}`}>{info.text}</span>;
  };

  return (
    <div>
      <div className="flex-between mb-4">
        <h1>üìú Î°úÍ∑∏</h1>
        <button className="btn btn-danger" onClick={handleClearLogs}>
          üóëÔ∏è Ïò§ÎûòÎêú Î°úÍ∑∏ ÏÇ≠Ï†ú
        </button>
      </div>

      {message && (
        <div className={`alert alert-${message.type}`}>
          {message.text}
          <button
            onClick={() => setMessage(null)}
            style={{ float: 'right', background: 'none', border: 'none', cursor: 'pointer' }}
          >
            ‚úï
          </button>
        </div>
      )}

      {/* ÌÜµÍ≥Ñ */}
      {stats && (
        <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(150px, 1fr))', gap: '16px', marginBottom: '24px' }}>
          <div className="card" style={{ textAlign: 'center', padding: '16px' }}>
            <div style={{ fontSize: '1.5rem', fontWeight: 'bold' }}>{stats.logs?.total || 0}</div>
            <div className="text-muted">Ï†ÑÏ≤¥ Î°úÍ∑∏</div>
          </div>
          <div className="card" style={{ textAlign: 'center', padding: '16px' }}>
            <div style={{ fontSize: '1.5rem', fontWeight: 'bold', color: 'var(--primary-color)' }}>
              {stats.logs?.last24h || 0}
            </div>
            <div className="text-muted">ÏµúÍ∑º 24ÏãúÍ∞Ñ</div>
          </div>
          <div className="card" style={{ textAlign: 'center', padding: '16px' }}>
            <div style={{ fontSize: '1.5rem', fontWeight: 'bold', color: 'var(--error-color)' }}>
              {stats.errors?.total || 0}
            </div>
            <div className="text-muted">Ï†ÑÏ≤¥ ÏóêÎü¨</div>
          </div>
          <div className="card" style={{ textAlign: 'center', padding: '16px' }}>
            <div style={{ fontSize: '1.5rem', fontWeight: 'bold', color: 'var(--warning-color)' }}>
              {stats.logs?.byLevel?.WARN || 0}
            </div>
            <div className="text-muted">Í≤ΩÍ≥†</div>
          </div>
        </div>
      )}

      {/* ÌÉ≠ */}
      <div className="tabs">
        <button
          className={`tab ${activeTab === 'logs' ? 'active' : ''}`}
          onClick={() => setActiveTab('logs')}
        >
          üìã ÏùºÎ∞ò Î°úÍ∑∏
        </button>
        <button
          className={`tab ${activeTab === 'errors' ? 'active' : ''}`}
          onClick={() => setActiveTab('errors')}
        >
          ‚ö†Ô∏è ÏóêÎü¨ Î°úÍ∑∏
        </button>
      </div>

      {/* ÌïÑÌÑ∞ (ÏùºÎ∞ò Î°úÍ∑∏Îßå) */}
      {activeTab === 'logs' && (
        <div className="card" style={{ padding: '12px 16px' }}>
          <div className="flex gap-2">
            <button
              className={`btn ${filter === '' ? 'btn-primary' : 'btn-secondary'} btn-sm`}
              onClick={() => setFilter('')}
            >
              Ï†ÑÏ≤¥
            </button>
            <button
              className={`btn ${filter === 'INFO' ? 'btn-primary' : 'btn-secondary'} btn-sm`}
              onClick={() => setFilter('INFO')}
            >
              INFO
            </button>
            <button
              className={`btn ${filter === 'WARN' ? 'btn-primary' : 'btn-secondary'} btn-sm`}
              onClick={() => setFilter('WARN')}
            >
              WARN
            </button>
            <button
              className={`btn ${filter === 'ERROR' ? 'btn-primary' : 'btn-secondary'} btn-sm`}
              onClick={() => setFilter('ERROR')}
            >
              ERROR
            </button>
          </div>
        </div>
      )}

      {/* Î°úÍ∑∏ Î™©Î°ù */}
      <div className="card">
        {loading ? (
          <div className="loading">
            <div className="spinner"></div>
            <span>Î°úÎî© Ï§ë...</span>
          </div>
        ) : activeTab === 'logs' ? (
          logs.length === 0 ? (
            <p className="text-muted text-center" style={{ padding: '40px' }}>
              Î°úÍ∑∏Í∞Ä ÏóÜÏäµÎãàÎã§.
            </p>
          ) : (
            <div className="table-container">
              <table>
                <thead>
                  <tr>
                    <th>ÏãúÍ∞Ñ</th>
                    <th>Î†àÎ≤®</th>
                    <th>ÌÉúÍ∑∏</th>
                    <th>ÏöîÏ≤≠ ID</th>
                    <th>Î©îÏãúÏßÄ</th>
                  </tr>
                </thead>
                <tbody>
                  {logs.map(log => (
                    <tr key={log.id}>
                      <td className="text-muted" style={{ fontSize: '0.85rem', whiteSpace: 'nowrap' }}>
                        {new Date(log.timestamp).toLocaleString('ko-KR')}
                      </td>
                      <td>{getLevelBadge(log.level)}</td>
                      <td style={{ fontSize: '0.85rem' }}>{log.tag || '-'}</td>
                      <td style={{ fontFamily: 'monospace', fontSize: '0.8rem' }}>
                        {log.request_id ? log.request_id.slice(0, 8) + '...' : '-'}
                      </td>
                      <td style={{ maxWidth: '400px', overflow: 'hidden', textOverflow: 'ellipsis' }}>
                        {log.message}
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          )
        ) : (
          errors.length === 0 ? (
            <p className="text-muted text-center" style={{ padding: '40px' }}>
              ÏóêÎü¨ Î°úÍ∑∏Í∞Ä ÏóÜÏäµÎãàÎã§.
            </p>
          ) : (
            <div className="table-container">
              <table>
                <thead>
                  <tr>
                    <th>ÏãúÍ∞Ñ</th>
                    <th>ÏöîÏ≤≠ ID</th>
                    <th>Ìï®Ïàò</th>
                    <th>Î©îÏãúÏßÄ</th>
                  </tr>
                </thead>
                <tbody>
                  {errors.map(error => (
                    <tr key={error.id}>
                      <td className="text-muted" style={{ fontSize: '0.85rem', whiteSpace: 'nowrap' }}>
                        {new Date(error.timestamp).toLocaleString('ko-KR')}
                      </td>
                      <td style={{ fontFamily: 'monospace', fontSize: '0.8rem' }}>
                        {error.request_id ? error.request_id.slice(0, 8) + '...' : '-'}
                      </td>
                      <td style={{ fontSize: '0.85rem' }}>{error.func_name || '-'}</td>
                      <td>
                        <div style={{ color: 'var(--error-color)' }}>{error.message}</div>
                        {error.stack && (
                          <details style={{ marginTop: '4px' }}>
                            <summary style={{ cursor: 'pointer', fontSize: '0.8rem', color: 'var(--text-secondary)' }}>
                              Ïä§ÌÉù Ìä∏Î†àÏù¥Ïä§
                            </summary>
                            <pre style={{
                              fontSize: '0.75rem',
                              background: '#f5f5f5',
                              padding: '8px',
                              borderRadius: '4px',
                              overflow: 'auto',
                              maxHeight: '150px'
                            }}>
                              {error.stack}
                            </pre>
                          </details>
                        )}
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          )
        )}
      </div>
    </div>
  );
}

export default Logs;
