import React, { useState, useEffect } from 'react';
import { promptsApi } from '../api';

function Prompts() {
  const [prompts, setPrompts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [message, setMessage] = useState(null);
  const [selectedPrompt, setSelectedPrompt] = useState(null);
  const [editMode, setEditMode] = useState(false);
  const [formData, setFormData] = useState({
    prompt_key: '',
    title: '',
    prompt_text: '',
    active: true
  });

  useEffect(() => {
    loadPrompts();
  }, []);

  const loadPrompts = async () => {
    try {
      setLoading(true);
      const res = await promptsApi.getAll();
      setPrompts(res.data || []);
    } catch (error) {
      setMessage({ type: 'error', text: error.message });
    } finally {
      setLoading(false);
    }
  };

  const handleSelect = (prompt) => {
    setSelectedPrompt(prompt);
    setFormData({
      prompt_key: prompt.prompt_key,
      title: prompt.title || '',
      prompt_text: prompt.prompt_text || '',
      active: prompt.active === 1
    });
    setEditMode(false);
  };

  const handleSave = async () => {
    try {
      if (selectedPrompt) {
        await promptsApi.update(selectedPrompt.prompt_key, {
          title: formData.title,
          prompt_text: formData.prompt_text,
          active: formData.active
        });
        setMessage({ type: 'success', text: 'ÌîÑÎ°¨ÌîÑÌä∏Í∞Ä Ï†ÄÏû•ÎêòÏóàÏäµÎãàÎã§.' });
      } else {
        await promptsApi.create(formData);
        setMessage({ type: 'success', text: 'ÌîÑÎ°¨ÌîÑÌä∏Í∞Ä ÏÉùÏÑ±ÎêòÏóàÏäµÎãàÎã§.' });
      }
      loadPrompts();
      setEditMode(false);
    } catch (error) {
      setMessage({ type: 'error', text: error.message });
    }
  };

  const handleDelete = async () => {
    if (!selectedPrompt) return;
    if (!window.confirm('Ïù¥ ÌîÑÎ°¨ÌîÑÌä∏Î•º ÏÇ≠Ï†úÌïòÏãúÍ≤†ÏäµÎãàÍπå?')) return;

    try {
      await promptsApi.delete(selectedPrompt.prompt_key);
      setMessage({ type: 'success', text: 'ÌîÑÎ°¨ÌîÑÌä∏Í∞Ä ÏÇ≠Ï†úÎêòÏóàÏäµÎãàÎã§.' });
      setSelectedPrompt(null);
      loadPrompts();
    } catch (error) {
      setMessage({ type: 'error', text: error.message });
    }
  };

  const handleNew = () => {
    setSelectedPrompt(null);
    setFormData({
      prompt_key: '',
      title: '',
      prompt_text: '',
      active: true
    });
    setEditMode(true);
  };

  const getPromptTypeLabel = (key) => {
    if (key === 'MASTER_PROMPT') return 'üéØ ÎßàÏä§ÌÑ∞';
    if (key === 'PASSAGE_MASTER') return 'üìÑ ÏßÄÎ¨∏ ÎßàÏä§ÌÑ∞';
    if (key.startsWith('P')) return 'üìù ÏßÄÎ¨∏Ïö©';
    if (/^\d+$/.test(key)) return `üìã RC${key}`;
    return 'Í∏∞ÌÉÄ';
  };

