import React, { useState, useEffect } from 'react';
import { itemsApi, logsApi, healthCheck } from '../api';

function Dashboard() {
  const [stats, setStats] = useState(null);
  const [logStats, setLogStats] = useState(null);
  const [recentItems, setRecentItems] = useState([]);
  const [serverStatus, setServerStatus] = useState('checking');
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    loadData();
  }, []);

  const loadData = async () => {
    try {
      // ÏÑúÎ≤Ñ ÏÉÅÌÉú Ï≤¥ÌÅ¨
      await healthCheck();
      setServerStatus('online');

      // Î¨∏Ìï≠ ÏöîÏ≤≠ ÌÜµÍ≥Ñ
      const requestsRes = await itemsApi.getRequests({ limit: 5 });
      setRecentItems(requestsRes.data || []);

      // ÏÉÅÌÉúÎ≥Ñ Ïπ¥Ïö¥Ìä∏ Í≥ÑÏÇ∞
      const allRequests = await itemsApi.getRequests({ limit: 1000 });
      const statusCounts = {
        total: allRequests.pagination?.total || 0,
        pending: 0,
        running: 0,
        ok: 0,
        fail: 0
      };

      (allRequests.data || []).forEach(req => {
        const status = (req.status || '').toLowerCase();
        if (statusCounts[status] !== undefined) {
          statusCounts[status]++;
        }
      });

      setStats(statusCounts);

      // Î°úÍ∑∏ ÌÜµÍ≥Ñ
      const logStatsRes = await logsApi.getStats();
      setLogStats(logStatsRes.data);

    } catch (error) {
      console.error('Îç∞Ïù¥ÌÑ∞ Î°úÎìú Ïò§Î•ò:', error);
      setServerStatus('offline');
    } finally {
      setLoading(false);
    }
  };

  const getStatusBadge = (status) => {
    const statusMap = {
      PENDING: { class: 'badge-pending', text: 'ÎåÄÍ∏∞ Ï§ë' },
      RUNNING: { class: 'badge-running', text: 'Ïã§Ìñâ Ï§ë' },
      OK: { class: 'badge-ok', text: 'ÏÑ±Í≥µ' },
      FAIL: { class: 'badge-fail', text: 'Ïã§Ìå®' }
    };
    const info = statusMap[status] || { class: 'badge-pending', text: status };
    return <span className={`badge ${info.class}`}>{info.text}</span>;
  };

  if (loading) {
    return (
      <div className="loading">
        <div className="spinner"></div>
        <span>Îç∞Ïù¥ÌÑ∞ Î°úÎî© Ï§ë...</span>
      </div>
    );
  }

  return (
    <div>
      <h1 style={{ marginBottom: '24px' }}>üìä ÎåÄÏãúÎ≥¥Îìú</h1>

      {/* ÏÑúÎ≤Ñ ÏÉÅÌÉú */}
      <div className={`alert ${serverStatus === 'online' ? 'alert-success' : 'alert-error'}`}>
        ÏÑúÎ≤Ñ ÏÉÅÌÉú: {serverStatus === 'online' ? 'üü¢ Ïò®ÎùºÏù∏' : 'üî¥ Ïò§ÌîÑÎùºÏù∏'}
      </div>

      {/* ÌÜµÍ≥Ñ Ïπ¥Îìú */}
      <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(200px, 1fr))', gap: '16px', marginBottom: '24px' }}>
        <div className="card" style={{ textAlign: 'center' }}>
          <div style={{ fontSize: '2rem', fontWeight: 'bold', color: 'var(--primary-color)' }}>
            {stats?.total || 0}
          </div>
          <div className="text-muted">Ï†ÑÏ≤¥ ÏöîÏ≤≠</div>
        </div>
        <div className="card" style={{ textAlign: 'center' }}>
          <div style={{ fontSize: '2rem', fontWeight: 'bold', color: '#fbbc04' }}>
            {stats?.pending || 0}
          </div>
          <div className="text-muted">ÎåÄÍ∏∞ Ï§ë</div>
        </div>
        <div className="card" style={{ textAlign: 'center' }}>
          <div style={{ fontSize: '2rem', fontWeight: 'bold', color: 'var(--success-color)' }}>
