import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { itemsApi } from '../api';

function ItemCreate() {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    item_no: 29,
    passage: '',
    level: 'Ï§ë',
    extra: '',
    chart_id: '',
    topic: '',
    passage_source: ''
  });
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState(null);

  // Î¨∏Ìï≠ Ïú†Ìòï Î™©Î°ù
  const itemTypes = [
    { no: 18, label: 'RC18 Í∏ÄÏùò Î™©Ï†Å' },
    { no: 19, label: 'RC19 Ïã¨Í≤Ω Î≥ÄÌôî' },
    { no: 20, label: 'RC20 ÌïÑÏûê Ï£ºÏû•' },
    { no: 21, label: 'RC21 Ìï®Ï∂ï ÏùòÎØ∏' },
    { no: 22, label: 'RC22 Í∏ÄÏùò ÏöîÏßÄ' },
    { no: 23, label: 'RC23 Í∏ÄÏùò Ï£ºÏ†ú' },
    { no: 24, label: 'RC24 Í∏ÄÏùò Ï†úÎ™©' },
    { no: 25, label: 'RC25 ÎèÑÌëú Ïù¥Ìï¥' },
    { no: 26, label: 'RC26 ÎÇ¥Ïö© ÏùºÏπò (Ïù∏Î¨º)' },
    { no: 27, label: 'RC27 ÎÇ¥Ïö© ÏùºÏπò (ÏïàÎÇ¥Î¨∏)' },
    { no: 28, label: 'RC28 Ïñ¥Ìúò' },
    { no: 29, label: 'RC29 Ïñ¥Î≤ï' },
    { no: 30, label: 'RC30 ÏßÄÏπ≠ Ï∂îÎ°†' },
    { no: 31, label: 'RC31 ÎπàÏπ∏ (Ïñ¥Íµ¨)' },
    { no: 32, label: 'RC32 ÎπàÏπ∏ (Ïñ¥Íµ¨)' },
    { no: 33, label: 'RC33 ÎπàÏπ∏ (Î¨∏Ïû•)' },
    { no: 34, label: 'RC34 ÎπàÏπ∏ (Î¨∏Ïû•)' },
    { no: 35, label: 'RC35 Î¨¥Í¥ÄÌïú Î¨∏Ïû•' },
    { no: 36, label: 'RC36 Í∏ÄÏùò ÏàúÏÑú' },
    { no: 37, label: 'RC37 Í∏ÄÏùò ÏàúÏÑú' },
    { no: 38, label: 'RC38 Î¨∏Ïû• ÏÇΩÏûÖ' },
    { no: 39, label: 'RC39 Î¨∏Ïû• ÏÇΩÏûÖ' },
    { no: 40, label: 'RC40 ÏöîÏïΩÎ¨∏ ÏôÑÏÑ±' },
  ];

  const levels = ['Ìïò', 'Ï§ëÌïò', 'Ï§ë', 'Ï§ëÏÉÅ', 'ÏÉÅ'];

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: name === 'item_no' ? parseInt(value) : value
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      setLoading(true);
      const res = await itemsApi.createRequest(formData);
      setMessage({ type: 'success', text: 'ÏöîÏ≤≠Ïù¥ ÏÉùÏÑ±ÎêòÏóàÏäµÎãàÎã§. Î¨∏Ìï≠ ÏÉùÏÑ±ÏùÑ ÏãúÏûëÌï©ÎãàÎã§...' });

      // Î∞îÎ°ú ÏÉùÏÑ± ÏãúÏûë
      try {
        const genRes = await itemsApi.generate(res.data.requestId);
        setMessage({
          type: genRes.data.validationResult === 'PASS' ? 'success' : 'warning',
          text: `Î¨∏Ìï≠ ÏÉùÏÑ± ÏôÑÎ£å: ${genRes.data.validationResult}`
        });

        setTimeout(() => {
          navigate('/items');
        }, 2000);
      } catch (genError) {
        setMessage({ type: 'error', text: 'Î¨∏Ìï≠ ÏÉùÏÑ± Ï§ë Ïò§Î•ò: ' + genError.message });
      }
    } catch (error) {
      setMessage({ type: 'error', text: error.message });
    } finally {
      setLoading(false);
    }
  };

  const handleSaveOnly = async () => {
    try {
